import { useState } from "react";
import axios from "axios";
import { Link, useNavigate } from "react-router-dom";

const Signup = () => {
  const [data, setData] = useState({
    firstName: "",
    lastName: "",
    email: "",
    password: "",
  });
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const handleChange = ({ currentTarget: input }) => {
    setData({ ...data, [input.name]: input.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const url = "http://localhost:8080/api/users";
      const { data: res } = await axios.post(url, data);
      navigate("/login");
      console.log(res.message);
    } catch (error) {
      if (
        error.response &&
        error.response.status >= 400 &&
        error.response.status <= 500
      ) {
        setError(error.response.data.message);
      }
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-green-400 to-blue-500">
      <div className="w-full max-w-4xl rounded-lg shadow-xl overflow-hidden bg-white">
        <div className="grid grid-cols-2">
          <div className="bg-green-500 py-12 px-10 rounded-l-lg flex flex-col justify-center items-center text-white">
            <h1 className="text-4xl font-bold mb-4">Welcome Back</h1>
            <p className="text-sm">Â© 2024 YourCompanyName</p>
          </div>
          <div className="py-12 px-10 rounded-r-lg flex flex-col justify-center items-center">
            <h1 className="text-4xl font-bold mb-4">Create Account</h1>
            <form onSubmit={handleSubmit} className="w-full">
              <input
                type="text"
                placeholder="First Name"
                name="firstName"
                onChange={handleChange}
                value={data.firstName}
                required
                className="input"
              />
              <input
                type="text"
                placeholder="Last Name"
                name="lastName"
                onChange={handleChange}
                value={data.lastName}
                required
                className="input"
              />
              <input
                type="email"
                placeholder="Email"
                name="email"
                onChange={handleChange}
                value={data.email}
                required
                className="input"
              />
              <input
                type="password"
                placeholder="Password"
                name="password"
                onChange={handleChange}
                value={data.password}
                required
                className="input"
              />
              {error && <div className="error_msg">{error}</div>}
              <button type="submit" className="green_btn">
                Sign Up
              </button>
              <p className="text-sm mt-4">Â© 2024 YourCompanyName</p>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Signup;
