import { useContext } from "react";
import { NavLink } from "react-router-dom";
import { GlobalContext } from "../../context";


export default function Navbar() {
  const { searchParam, setSearchParam, handleSubmit } = useContext(GlobalContext);

  const handleLogout = () => {
    localStorage.removeItem("token");
    window.location.reload();
  };

  console.log(searchParam);

  return (
    <nav className="flex justify-between items-center py-4 px-8 container mx-auto bg-white shadow-lg rounded-lg flex-col lg:flex-row gap-5 lg:gap-0">
      <div className="flex items-center gap-2">

        <h2 className="text-3xl font-bold text-gray-800 tracking-wide">
          <NavLink to={"/"}>Mitt Arv Assign</NavLink>
        </h2>
      </div>
      <form onSubmit={handleSubmit} className="relative w-full lg:w-auto">
        <input
          type="text"
          name="search"
          value={searchParam}
          onChange={(event) => setSearchParam(event.target.value)}
          placeholder="Search..."
          className="bg-white p-3 px-8 rounded-full outline-none lg:w-96 shadow-md text-gray-800 focus:ring-2 focus:ring-blue-500 transition-all duration-300"
        />
      </form>
      <ul className="flex gap-5 text-lg font-medium">
        <li>
          <NavLink
            to={"/Home"}
            className="text-black hover:text-blue-600 transition duration-300"
          >
            Home
          </NavLink>
        </li>
        <li>
          <NavLink
            to={"/favorites"}
            className="text-black hover:text-blue-600 transition duration-300"
          >
            Favorites
          </NavLink>
        </li>
        <li>
          <NavLink
            to={"/login"}
            className="text-black hover:text-blue-600 transition duration-300"
            onClick={handleLogout}
          >
            Logout
          </NavLink>
        </li>
      </ul>
    </nav>
  );
}
